import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../utils/auth';
import './Login.css';

const Login = () => {
    const [credentials, setCredentials] = useState({
        username: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const { login, isAuthenticated, user } = useAuth();

    // Redirect if already logged in
    useEffect(() => {
        if (isAuthenticated && user) {
            if (user.roles.includes('ROLE_ADMIN')) {
                navigate('/dashboard');
            } else {
                navigate('/ibans');
            }
        }
    }, [isAuthenticated, navigate, user]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCredentials(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const userData = await login(credentials);

            // Redirect based on role
            if (userData.roles.includes('ROLE_ADMIN')) {
                navigate('/dashboard');
            } else {
                navigate('/ibans');
            }
        } catch (err) {
            console.error('Login error:', err);
            setError('Autentificare eșuată. Verificați numele de utilizator și parola.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <h1>IBAN Management System</h1>
            <p className="login-subtitle">Vă rugăm să vă autentificați pentru a continua</p>

            {error && <div className="login-error">{error}</div>}

            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="username">Nume utilizator</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={credentials.username}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="password">Parolă</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={credentials.password}
                        onChange={handleChange}
                        required
                    />
                </div>

                <button
                    type="submit"
                    className="login-button"
                    disabled={loading}
                >
                    {loading ? 'Se procesează...' : 'Autentificare'}
                </button>
            </form>

            <div className="login-credentials">
                <h3>Credențiale test:</h3>
                <div className="credential-group">
                    <p>Admin:</p>
                    <p>admin / admin123</p>
                </div>
                <div className="credential-group">
                    <p>Operator:</p>
                    <p>operator / operator123</p>
                </div>
                <div className="credential-group">
                    <p>Operator Raion:</p>
                    <p>raion / raion123</p>
                </div>
            </div>
        </div>
    );
};

export default Login;
